INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-6 12:00:00') as dateinserted, raw_6 as raw, unique_6 as uniq, declined_6 as `declined` FROM qu_pap_monthlyclicks WHERE DAY(LAST_DAY(month)) >= 6 AND (raw_6 > 0 OR unique_6 > 0 OR declined_6 > 0);