INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-11 12:00:00') as dateinserted, raw_11 as raw, unique_11 as uniq, declined_11 as `declined` FROM qu_pap_monthlyclicks WHERE DAY(LAST_DAY(month)) >= 11 AND (raw_11 > 0 OR unique_11 > 0 OR declined_11 > 0);